const execFile = require('child_process').execFile;
const env = require('./env');

function deleteConnection(config, ap, callback) {
  const params = ['wlan', 'delete', 'profile', `name="${ap.ssid}"`];
  if (config.iface) {
    params.push(`interface="${config.iface}"`);
  }

  execFile('netsh', params, { env }, err => {
    callback && callback(err);
  });
}

module.exports = config => {
  return (ap, callback) => {
    if (callback) {
      deleteConnection(config, ap, callback);
    } else {
      return new Promise((resolve, reject) => {
        deleteConnection(config, ap, err => {
          if (err) {
            reject(err);
          } else {
            resolve();
          }
        });
      });
    }
  };
};